	<li><span>           var b = canvasData.data[idx + <span class="number">2</span><span>];    </span></span></li>
	<li class="alt"><span>              var nWidth = pattern_width[m_VideoType];  </span></li>
	<li><span>              var nHeight = pattern_height[m_VideoType];  </span></li>
	<li class="alt"><span>              var index = nWidth * (y % nHeight) + (x % nWidth);  </span></li>
	<li><span>  </span></li>
	<li class="alt"><span>              index = pattern[index];  </span></li>
	<li><span>              <span class="keyword">if</span><span> (index == </span><span class="number">0</span><span>)  </span></span></li>
	<li class="alt"><span>               var   r = fclamp0255(<span class="number">2</span><span> * r);  </span></span></li>
	<li><span>              <span class="keyword">if</span><span> (index == </span><span class="number">1</span><span>)  </span></span></li>
	<li class="alt"><span>               var   g = fclamp0255(<span class="number">2</span><span> * g);  </span></span></li>
	<li><span>              <span class="keyword">if</span><span> (index == </span><span class="number">2</span><span>)  </span></span></li>
	<li class="alt"><span>               var   b = fclamp0255(<span class="number">2</span><span> * b);  </span></span></li>
	<li><span>                    </span></li>
	<li class="alt"><span>            <span class="comment">// assign gray scale value  </span><span>  </span></span></li>
	<li><span>            canvasData.data[idx + <span class="number">0</span><span>] = r; </span><span class="comment">// Red channel  </span><span>  </span></span></li>
	<li class="alt"><span>            canvasData.data[idx + <span class="number">1</span><span>] = g; </span><span class="comment">// Green channel  </span><span>  </span></span></li>
	<li><span>            canvasData.data[idx + <span class="number">2</span><span>] = b; </span><span class="comment">// Blue channel  </span><span>  </span></span></li>
	<li class="alt"><span>            canvasData.data[idx + <span class="number">3</span><span>] = </span><span class="number">255</span><span>; </span><span class="comment">// Alpha channel  </span><span>  </span></span></li>
	<li><span>            <span class="comment">// 加上黑色的边框  </span><span>  </span></span></li>
	<li class="alt"><span>            <span class="keyword">if</span><span>(x &lt; </span><span class="number">8</span><span> || y &lt; </span><span class="number">8</span><span> || x &gt; (canvasData.width - </span><span class="number">8</span><span>) || y &gt; (canvasData.height - </span><span class="number">8</span><span>))     </span></span></li>
	<li><span>            {    </span></li>
	<li class="alt"><span>              canvasData.data[idx + <span class="number">0</span><span>] = </span><span class="number">0</span><span>;    </span></span></li>
	<li><span>              canvasData.data[idx + <span class="number">1</span><span>] = </span><span class="number">0</span><span>;    </span></span></li>
	<li class="alt"><span>              canvasData.data[idx + <span class="number">2</span><span>] = </span><span class="number">0</span><span>;    </span></span></li>
	<li><span>            }    </span></li>
	<li class="alt"><span>         }    </span></li>
	<li><span>    }   </span></li>
</ol>
